        val tvNoFound = findViewById<TextView>(R.id.tvNoFound)
        val lista = findViewById<GridView>(R.id.gvLista)
        var listaMonstruos = listOf(monstruo, monstruo2, monstruo3, monstruo4, monstruo5, monstruo6, monstruo7, monstruo8, monstruo9, monstruo10, monstruo11, monstruo12, monstruo13, monstruo14, monstruo15, monstruo16, monstruo17, monstruo18, monstruo19, monstruo20, monstruo21, monstruo22, monstruo23, monstruo24, monstruo25, monstruo26, monstruo27, monstruo28, monstruo29, monstruo30, monstruo31, monstruo32, monstruo33, monstruo34, monstruo35, monstruo36, monstruo37, monstruo38, monstruo39, monstruo40, monstruo41, monstruo42, monstruo43, monstruo44, monstruo45, monstruo46, monstruo47, monstruo48, monstruo49, monstruo50, monstruo51, monstruo52, monstruo53, monstruo54, monstruo55, monstruo56, monstruo57, monstruo58, monstruo59, monstruo60, monstruo61, monstruo62, monstruo63, monstruo64, monstruo65, monstruo66, monstruo67, monstruo68, monstruo69, monstruo70, monstruo71, monstruo72, monstruo73, monstruo74, monstruo75, monstruo76, monstruo77, monstruo78, monstruo79, monstruo80, monstruo81, monstruo82, monstruo83, monstruo84, monstruo85, monstruo86, monstruo87, monstruo88, monstruo89, monstruo90, monstruo91, monstruo92, monstruo93, monstruo94, monstruo95, monstruo96, monstruo97, monstruo98)
        listar(listaMonstruos, nombre!!, especie!!, tamano!!, elemento!!, hostilidad)

        if (listaMonstruosFiltrada.isEmpty()) {
            tvNoFound.visibility = View.VISIBLE
            lista.visibility = View.GONE
        } else {
            tvNoFound.visibility = View.GONE
            lista.visibility = View.VISIBLE
            val adapter = MonsterAdapter(this, listaMonstruosFiltrada)
            lista.adapter = adapter
        }



        lista.setOnItemClickListener { parent, view, position, id ->
            val monstruoSelecionado = listaMonstruosFiltrada[position]

            var grito = MediaPlayer.create(this, monstruoSelecionado.grito!!)

            grito?.start()

            val intent = Intent(this,DetallesActivity::class.java)
            intent.putExtra("monstruo", listaMonstruosFiltrada[position])
            startActivity(intent)
        }
    }
    private lateinit var listaMonstruosFiltrada: List<item_Monstruo>

    private fun listar(listaMonstruos: List<item_Monstruo>, nombre:String, espe:String, tama:String, ele:String, hos:Int) {
        if (!nombre.isEmpty()) {
            listaMonstruosFiltrada = listaMonstruos.filter { it.nombre.uppercase().contains(nombre!!.uppercase()) }
        } else {
            if (hos == 0) {
                listaMonstruosFiltrada = listaMonstruos.filter { it.nombre.uppercase().contains(nombre!!.uppercase()) && espe.contains(it.especies) && ele.contains(it.elemento) && tama.contains(it.tamano)}
            } else {
                listaMonstruosFiltrada = listaMonstruos.filter { it.nombre.uppercase().contains(nombre!!.uppercase()) && espe.contains(it.especies) && it.elemento.contains(ele!!) && tama.contains(it.tamano) && it.hostil == hos}
            }
        }
    }
}